css:
.pricetag {
  width: 84mm;
  height: 61mm;
  display: flex;
  border: 1px solid #000;
  border-radius: 5px;
  margin: 10px 0;
}

.image {
  width: 40%;
  padding: 5px;
}

.image img {
  width: 100%;
  height: auto;
  padding-top: 50px;
}

.details {
  width: 60%;
  padding: 4px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.name {
  font-size: 1.2em;
  margin: 0;
}

.description {
  margin: 5px 0;
}

.code {
  margin: 5px 0;
  padding-top: 30px;
  text-align: center;
}

.price {
  font-weight: bold;
  margin: 5px 0;
  text-align: center;
  background-color: #4CAF50; /* Green background color */
  color: white; /* Text color */
  padding: 5px; /* Add some padding to make the tag more visible */
  border-radius: 25px; /* Add rounded corners to the tag */
}

html:
{% for row in doc.items %}
<div class="pricetag">
  <div class="image">
    <img src="{{row.item_image}}" alt="Item Image">
    <p class="code"><b>SKU: {{ row.item }}</b></p> 
  </div>
  <div class="details">
    <h3 class="name">{{ row.item_name }}</h3>
    <p class="description">{{ row.item_description }}</p><br>
    <h3 class="price">
            {% if row.sales_uom and frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.sales_uom}, "price_list_rate") %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.sales_uom}, "price_list_rate") }}<br>VEP/{{row.sales_uom}}
            {% elif row.default_uom and frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.default_uom}, "price_list_rate") %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.default_uom}, "price_list_rate") }}<br>VEP/{{row.default_uom}}
            {% else %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1}, "price_list_rate") }}<br>VEP
            {% endif %}</h3>
  </div>
</div>
{% endfor %}
