css:
.pricetag {
  width: 84mm;
  height: 61mm;
  display: flex;
  border: 1px solid #000;
  border-radius: 5px;
  margin: 10px 0;
}

.image {
  width: 40%;
  padding: 5px;
}

.image img {
  width: 100%;
  height: 85%;
  padding-top: 5px;
}

.details {
  width: 60%;
  padding: 4px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.name {
  font-size: 1.2em;
  margin: 0;
}

.description {
  margin: 5px 0;
  padding-top: 12px;
}

.code {
  margin: 5px 0;
  padding-top: 3px;
  text-align: center;
}
.price {
	font-family: Calibri;
	background: #000000;
	display: flex;
	color: #fff;
	position: relative;
	padding: 10px;
	border-top-left-radius: 4px;
	border-bottom-left-radius: 4px;
	margin: 0 60px 0 8px;
	text-decoration: none;
	text-align: center;
}

.price:hover {
	background-color: #739fe4;
}

.price:before {
	background: #fff;
    width: 25px;
    height: 25px;
    content: "";
    display: inline-block;
    border-radius: 20px;
    margin: 11px 13px 0px -23px;
}

.price:after {
	display: inline-block;
	border: 34px solid;
	border-color: transparent transparent transparent #000000;
	height: 0;
	width: 0;
	position: absolute;
	right: -67px;
	top: -1px;
	content: "";
	display: inline-block;
}


.price:hover:after {
	border-color: transparent transparent transparent #739fe4;
}

html:
{% for row in doc.items %}
<div class="pricetag">
  <div class="image">
    <img src="{{row.item_image}}" alt="Item Image">
    <p class="code"><b>SKU: {{ row.item }}</b></p> 
  </div>
  <div class="details">
    <h3 class="name">{{ row.item_name }}</h3>
    <p class="description">{{ row.item_description }}</p><br>
    <h3 class="price">
            {% if row.sales_uom and frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.sales_uom, "price_list":"Standard Selling"}, "price_list_rate") %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.sales_uom, "price_list":"Standard Selling"}, "price_list_rate") }}<br>VEP/{{row.sales_uom}}
            {% elif row.default_uom and frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.default_uom, "price_list":"Standard Selling"}, "price_list_rate") %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1, "uom":row.default_uom, "price_list":"Standard Selling"}, "price_list_rate") }}<br>VEP/{{row.default_uom}}
            {% else %}
            {{ frappe.db.get_value("Item Price", {"item_code":row.item, "selling":1,"price_list":"Standard Selling"}, "price_list_rate") }}<br>VEP
            {% endif %}</h3>
  </div>
</div>
{% endfor %}
